namespace CefSharp.fastBOT.Models{    public class ProxyConfig    {        public string Host { get; set; }        public int Port { get; set; }        public string Scheme { get; set; } = "http";        public string Username { get; set; }        public string Password { get; set; }        public string Name { get; set; }        public override string ToString()        {            return $"{Scheme}://{Host}:{Port}";        }        public string ToConnectionString()        {            if (!string.IsNullOrEmpty(Username))            {                return $"{Host}:{Port}:{Username}:{Password}";            }            return $"{Host}:{Port}";        }    }}